import React, { useState, useEffect } from "react";
import { /*Link,*/ useNavigate } from "react-router-dom";
import axios from "axios";
import { profileRoute } from "./APIRoutes";
import "./interests.css";


export default function MoreInfo() {

    const navigate = useNavigate(),
        [user, setUser] = useState(undefined),
        [userName, setUserName] = useState(undefined);

    const [values, setValues] = useState({
        bio: "",
        interests: "",
    });

    useEffect(() => {
        async function fetchData() {
            if (!localStorage.getItem("nlpchat-user")) {
                navigate("/login");
            } else {
                const data = await JSON.parse(localStorage.getItem("nlpchat-user"));
                setUser(data);
                setUserName(data.fname);
            }
        }
        fetchData();
    }, [navigate]);


    const handleChange = (event) => {
        setValues({ ...values, [event.target.name]: event.target.value });
    };    

    const handleValidation = () => {
        const { bio, interests } = values;
        if (bio.length < 5) {
            alert("Bio is too short.");
            return false;
        }
        else if (interests.length < 3) {
            alert("Select at least three interests",);
            return false;   
        }
        return true;
    };

    const handleSubmit = async (event) => {
        event.preventDefault();
        if (handleValidation()) {
            const { bio, interests } = values;
            await axios.post(`${setBioRoute}/${user._id}`, {
                bio,
                interests,
            });

            alert("User created");
            navigate("/contacts");
        };
    };



    return (
        <>
        <nav>
            <a href="./Welcome.jsx">
            <img src="Logo.svg" alt="Logo" />
            </a>
            <ul class="main-nav">
            <li class="push">
                <a href="">Log ut</a>
            </li>
            </ul>
        </nav>
        <section id="first">
            <h1>Snart ferdig. Fortell oss litt om deg selv</h1>
            <img class="profile-icon" src="profile.svg" alt="profile-icon" />
            <form class="box" id="">
                <label class="text">Om Meg</label>
                <textarea></textarea>
                <h2>Intresser</h2>
                <div class="btns-odd">
                    <button class="intress-btn">Mat</button>
                    <button class="intress-btn">Knust</button>
                    <button class="intress-btn">Litratur</button>
                </div>
                <div class="btns-even">
                    <button class="intress-btn">Idrett</button>
                    <button class="intress-btn">Dyr</button>
                    <button class="intress-btn">Språk</button>
                    <button class="intress-btn">Musikk</button>
                </div>
                <div class="showMore">
                    <a href="">
                    <img src="pluss.svg" alt="pluss-icon" />
                    </a>
                    <button>Vis flere</button>
                </div>
            </form>
        </section>
        <div class="btns">
            <button>Tilbake</button>
            <a href="contacts.html">
                <button type="submit" form="infoForm">Gå Videre</button>
            </a>
        </div>

        <footer>
            <p>Laget av Rami, Narges, Aina Og Fatima</p>
        </footer>
        </>
    );
}
